mapper.select:
SELECT YEAR_MONTH yearMonth, --年月 city_code cityCode, --城市公司编码 city_name cityName, --城市公司名称 project_code projectCode, --项目编码 project_name projectName, --项目名称 JOB_CONTENT jobContent, --工作内容 JOB_TARGET jobTarget, --工作目标 JOB_type jobType, --工作类型 p_end_DATE pEndDate, --计划结束时间 A_end_DATE aEndDate, --实际结束时间 JOB_STATUS jobStatus, --工作状态 JOB_PROCESSING jobProcessing, --当前进展 DUTY_DEPT dutyDept, --责任部门 DUTY_USER dutyUser, --责任人 FB_USER fbUser --反馈人 FROM FACT_HNA_IMPORTANT_LIST
city_code =#{cityCode}
AND JOB_type=#{jobType}
AND YEAR_MONTH =#{yearMonth}
AND JOB_PROCESSING =#{jobProcessing}
